vec4 Effect2(vec2 uv) {
    // Create two sine waves at different angles and frequencies
    float wave1 = sin(uv.x * 8.0 + iTime * 2.0);
    float wave2 = cos((uv.y - 0.5) * 6.0 + iTime * 3.0 + PI/4.0);

    // Combine waves and add noise for complexity
    vec2 st = uv;
    float noiseVal = noise(st * 10.0 + iTime);
    float combined = (wave1 + wave2) / 2.0 + noiseVal * 0.5;

    // Create dynamic color transitions using HSL
    vec3 hslColor = vec3(
        mod(iTime * 0.5 + uv.x * 2.0, 1.0),  // Hue
        0.7 + abs(sin(iTime)) * 0.3,          // Saturation
        0.5 + combined * 0.4                 // Lightness
    );

    // Convert to RGB and add turbulence effect
    vec3 color = hsl2rgb(hslColor);
    color *= turbulent(vec3(uv * 10.0, iTime)) * 0.5 + 0.5;

    return vec4(color, 1.0);
}
