vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float r = length(uv);
    float theta = atan2(uv.y, uv.x);
    // Create rotating color wheels using HSL colors
    vec3 wheel1 = hsl(theta * 0.5 + iTime * 2.0, 0.7, 0.6);
    vec3 wheel2 = hsl(theta * 1.5 - iTime * 1.5, 0.8, 0.5);

    // Create pulsating rings using noise and turbulence
    float ring1 = sin(r * 4.0 + iTime) * 0.5 + 0.5;
    float ring2 = sin(r * 6.0 - iTime * 0.5) * 0.3 + 0.7;

    // Combine wheels and rings with noise
    vec3 color = mix(wheel1, wheel2, smoothstep(0.5, 1.0, r));
    color += mix(ring1, ring2, smoothstep(0.3, 0.8, r));

    // Add turbulence effect
    float turb = turbulent(vec3(uv * 4.0 + iTime, 0.0)) * 0.2;
    color += turb;
    return vec4(color, 1.0);
}
