vec4 Effect2(vec2 uv) {
    vec2 z = vec2(0.0, 0.0);
    vec2 c = vec2((uv.x - 0.5) * 3.0, (uv.y - 0.5) * 3.0);
    c.x += sin(iTime) * 0.5;

    float i = 0.0;
    for(; i < 20.0; i++) {
        if(length(z) > 2.0) break;

        vec2 new_z;
        new_z.x = z.x*z.x - z.y*z.y + c.x;
        new_z.y = 2.0 * z.x * z.y + c.y;
        z = new_z;
    }

    float noiseVal = noise(uv * 100.0);
    vec3 color = mix(vec3(0.0, 0.5, 1.0), vec3(1.0, 0.2, 0.8), sin(iTime + i / 5.0) * 0.5 + 0.5);
    color *= noiseVal * (i < 20.0 ? 0.8 : 1.0);

    return vec4(color, 1.0);
}
