vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create a base position with some noise offset
    vec2 pos = uv * 2.0 - 1.0;
    pos += sin(iTime + pos.xyx * 6.0) * 0.5;

    // Add multiple layers of points at different scales
    for(float i = 0.0; i < 3.0; i++) {
        float scale = pow(2.0, i);
        vec2 p = floor(pos * scale + 0.5) / scale;

        // Calculate distance from center
        float d = length(p - pos);

        // Create a point pattern with noise modulation
        float n = noise(vec3(p * 10.0, iTime));
        float fade = smoothstep(0.0, 0.5, n) * smoothstep(0.0, 0.2, d);

        // Add color with HSL to RGB conversion
        vec3 c = hsl(iTime + i * 0.3, 1.0, 0.5 + fade * 0.5);
        color += c * fade;
    }

    // Create a trail effect by mixing current and previous colors
    color *= 0.8; // Decay factor

    return vec4(color, 1.0);
}
