vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float theta = atan2(uv.y, uv.x);
    theta += iTime * 0.5; // Rotate over time

    // Create radial gradient
    float r = length(uv);
    float gradient = sin(r * pi); // Smooth transition from center to edge

    // Create color wheel effect with HSL colors
    vec3 hslColor = hsl(theta, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add pulsing effect
    float pulse = sin(iTime) * 0.3;
    rgbColor *= (0.5 + pulse);

    // Add dynamic RGB shifts
    vec3 rgbShifts = vec3(
        sin(iTime + 0.0) * 0.2,
        sin(iTime + 2.0) * 0.2,
        sin(iTime + 4.0) * 0.2
    );

    // Combine effects
    vec3 finalColor = rgbColor * gradient;
    finalColor += rgbShifts * 0.5; // Add dynamic shifts

    return vec4(clamp(finalColor, 0.0, 1.0), 1.0);
}
