vec4 Effect2(vec2 uv) {
    // Create dynamic sine wave patterns
    float time = iTime * 0.5;

    // Wave parameters
    vec2 st = uv * 10.0 + time;
    float waveX = sin(st.x * 0.5 + time) * 0.5 + 0.5;
    float waveY = sin(st.y * 0.7 - time) * 0.5 + 0.5;

    // Combine waves and add turbulence
    float d = (waveX + waveY) * 0.5;
    d += turbulent(vec2(uv * 4.0 + time)) * 0.3;

    // Convert to HSL color with dynamic shifts
    vec3 hslColor = vec3(
        mod(time * 0.1, 1.0),  // Hue
        0.7 + d * 0.3,       // Saturation
        0.5                  // Lightness
    );

    // Convert to RGB and output
    return vec4(hsl2rgb(hslColor), 1.0);
}
