vec4 Effect2(vec2 uv) {
    vec4 fragColor = vec4(0.0);

    // Create grid points
    vec2 st = floor(uv * 10.0 + 0.5);

    // Calculate distance from center of each square
    vec2 pos = (st - uv) * 0.1;
    float dist = length(pos);

    // Add rotation based on time
    float angle = atan2(pos.y, pos.x) + sin(iTime * 0.5);
    float rotatedDist = max(abs(cos(angle)), abs(sin(angle))) * 0.5;

    // Create square shape with varying opacity
    if (dist < 0.5) {
        fragColor.rgb = vec3(1.0);
        fragColor.a = turbulent(vec2(st)) * 0.5 + 0.5;
    }

    return fragColor;
}
