vec4 Effect2(vec2 uv) {
    // Create rotating coordinate system
    vec2 st = uv - 0.5;

    // Polar coordinates with rotation
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.1;

    // Sine wave pattern with multiple frequencies
    float wave = sin(r * 5.0 + theta * 3.0) * 0.5 +
                 sin(r * 7.0 - theta * 2.0) * 0.3 +
                 sin(r * 9.0 + theta * 4.0) * 0.2;

    // Color based on angle and time
    vec3 color = hsl(theta / (2.0 * PI), 0.8, 0.5);

    // Add noise for texture
    float noiseVal = noise(vec2(r, theta)) * 0.1;
    color *= 1.0 + noiseVal;

    return vec4(color, 1.0);
}
