vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float angle = atan(st.y, st.x) + iTime; // Rotate with time
    float radius = length(st);

    // Create rotating pattern using turbulence
    vec3 color = vec3(0.0);
    color += turbulent(vec3(radius * 5.0, angle * 2.0, iTime)) * 0.5;

    // Add RGB shifts
    vec3 rgbShifts = vec3(
        sin(angle + iTime) * 0.5 + 0.5,
        sin(angle - iTime) * 0.5 + 0.5,
        sin(angle + iTime * 2.0) * 0.5 + 0.5
    );

    // Combine patterns and shifts
    color *= rgbShifts;

    // Add pulsating intensity
    float pulse = sin(iTime * 3.0) * 0.5 + 0.5;
    color *= pulse;

    // Convert to RGB space with HSL transformation
    vec3 finalColor = hsl2rgb(vec3(
        angle * 0.1,
        0.7,
        length(color)
    ));

    return vec4(finalColor, 1.0);
}
