vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form
    float a = atan2(uv.y, uv.x);
    float r = length(uv);

    // Create rotating pattern by adding time-based rotation
    a += iTime * 0.5;

    // Create spiral effect using radius and angle
    float spiral = sin(a * 10.0 + r * 20.0) * 0.5 + 0.5;

    // Add pulsating color effect
    vec3 color = hsl(
        mod(iTime * 0.5 + r * 2.0, PI*2),  // Pulsating hue
        0.7,                                // Saturation
        0.5 + sin(r * 10.0 + iTime) * 0.3  // Radial brightness variation
    );

    // Add turbulence for more dynamic effect
    vec2 st = uv;
    float turb = turbulent(vec2(st.x*5.0, st.y*5.0)) * 0.3;
    turb += turbulent(vec2(a*2.0, r*2.0)) * 0.2;

    // Combine all elements
    vec3 finalColor = hsl2rgb(color) * spiral * (1.0 + turb);

    return vec4(finalColor, 1.0);
}
