vec4 Effect2(vec2 uv) {
    // Create a time-varying base pattern using noise
    vec3 noiseInput = vec3(uv * 5.0 + iTime * 0.1, iTime);
    float baseNoise = turbulent(noiseInput);

    // Generate color transitions using HSL
    vec3 colorBase = hsl(
        (baseNoise + sin(iTime) * 0.5 + 0.5) * 240.0,  // Hue variation over time
        0.7 + abs(sin(iTime * 0.5)) * 0.3,              // Saturation modulation
        0.5 + baseNoise * 0.5                         // Value modulation
    );

    // Add secondary layer of noise for texture
    vec2 uvScaled = uv * 10.0;
    float detailNoise = turbulent(vec3(uvScaled, iTime));

    // Combine layers with pulsing effect
    float pulse = sin(iTime) * 0.5 + 0.5;
    vec3 finalColor = mix(
        colorBase,
        hsl( (iTime * 2.0) % 360.0, 0.8, 0.6 ),
        detailNoise * pulse
    );

    // Add some value variation based on position and time
    finalColor *= 0.5 + turbulent(vec3(uvScaled * 0.5, iTime)) * 0.5;

    return vec4(finalColor, 1.0);
}
