vec4 Effect2(vec2 uv) {
    // Create a rotating color wheel effect
    float time = iTime * 0.5;

    // Generate base pattern using polar coordinates
    vec2 st = uv;
    float r = length(st);
    float theta = atan2(st.y, st.x);

    // Add noise and turbulence
    float pattern = sin(r * 10.0 + time) * cos(theta * 5.0 + time);
    pattern += turbulent(vec3(uv * 4.0, time)) * 0.5;

    // Color transformation using HSL
    vec3 color = hsl(
        mod(pattern * 0.5 + time, 1.0),  // Hue
        0.8,                              // Saturation
        0.5 + pattern * 0.2               // Lightness
    );

    return vec4(color, 1.0);
}
