vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime; // Add time for rotation

    // Create rotating sine wave pattern with multiple frequencies
    float wave = sin(theta * 2.0) * 0.5 +
                 sin(theta * 3.0) * 0.3 +
                 sin(r * 10.0 + iTime * 2.0) * 0.2;

    // Add noise and turbulence
    vec2 noiseScale = uv * 4.0;
    float turb = turbulent(noiseScale);
    wave += turb * 0.3;

    // Scale and offset for better contrast
    wave *= 1.5;
    wave = sin(wave + iTime) * 0.5 + 0.5; // Ensure positive values

    // Create color cycling effect using HSL
    vec3 hslColor = vec3(iTime * 0.2, 1.0, wave); // Hue cycles over time
    vec3 rgbColor = hsl2rgb(hslColor);

    return vec4(rgbColor, 1.0);
}
