vec4 Effect2(vec2 uv) {
    // Center coordinates
    vec2 st = uv - 0.5;

    // Time-based rotation
    float time = iTime * 0.5; // Speed control
    mat2 rot = mat2(cos(time), sin(time), -sin(time), cos(time));
    vec2 rotated = rot * st * 1.0;

    // Kaleidoscope effect using modulo and symmetry
    vec2 tile = abs(mod(rotated + 0.5, 1.0) - 0.5);
    vec2 mirrored = sign(tile) * (abs(tile) * 4.0 - 1.0);

    // Add noise for pulsating effect
    float noiseVal = noise(vec3(mirrored, time));
    mirrored *= 1.0 + sin(time + noiseVal * 2.0) * 0.5;

    // Color inversion based on noise
    vec3 color = hsl2rgb(vec3(
        mod(length(mirrored) * 4.0 + time, 1.0),
        0.7,
        0.6
    ));

    if (noiseVal > 0.5) {
        // Invert color
        color = vec3(1.0) - color;
    }

    return vec4(color, 1.0);
}
