vec4 Effect2(vec2 uv) {
    // Scale UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Create dynamic sine waves with different frequencies
    float wave1 = sin(4.0*st.x + iTime * 0.5) * cos(3.0*st.y + iTime);
    float wave2 = sin(5.0*st.x + iTime * 0.6) * cos(4.0*st.y + iTime * 0.8);

    // Combine waves with dynamic phase shift
    float combined = (wave1 + wave2) * 0.5;
    combined += sin(iTime * 0.2) * 0.3; // Add time-based modulation

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(combined * 6.0 + iTime, 1.0), // Hue variation
        0.7,                               // Saturation
        0.5 + noise(st * 4.0) * 0.2       // Lightness with noise variation
    );

    // Convert to RGB and add brightness modulation
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= 0.8 + abs(sin(iTime)) * 0.2;

    return vec4(rgbColor, 1.0);
}
