vec4 Effect2(vec2 uv) {
    // Scale UV coordinates for grid pattern
    vec2 st = uv * 20.0;

    // Get cell position and center offset
    vec2 pos = mod(st, 1.0) - 0.5;

    // Calculate distance from center
    float d = length(pos);

    // Create pulsing effect with time
    float pulse = sin(iTime + d * 10.0) * 0.5 + 0.5;

    // Add turbulence for dynamic effect
    float turb = turbulent(vec3(pos, iTime)) * 0.2;

    // Combine effects
    float circle = smoothstep(pulse + turb, pulse - turb, d);

    // Generate color based on distance and time
    vec3 color = hsl(
        sin(iTime * 0.5 + d * 2.0) * 0.5 + 0.5,
        1.0,
        circle * 0.8 + 0.2
    );

    return vec4(color, 1.0);
}
