vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float radius = length(st);
    float angle = atan2(st.y, st.x) + iTime * 0.5; // Add rotation

    // Create radial sine waves with different frequencies
    float wave1 = sin(radius * 3.0 + angle * 4.0);
    float wave2 = sin(radius * 5.0 + angle * 6.0);

    // Combine waves and add noise for texture
    float pattern = (wave1 + wave2) * 0.5;
    pattern += turbulent(uv * 2.0 + iTime); // Add pulsing noise

    // Create pulsing color transitions using HSL
    vec3 color = hsl(sin(iTime * 0.5) * 0.5 + 0.5, 1.0, 0.5);

    // Combine pattern with color and white for final effect
    float brightness = (pattern * 0.5 + 0.5) * 0.8;
    vec3 finalColor = mix(color, vec3(1.0), brightness);

    return vec4(finalColor, 1.0);
}
