vec4 Effect2(vec2 uv) {
    vec2 st = uv * 2.0 - 1.0;

    // Polar coordinates
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5;

    // Create rotating pattern using sine waves
    float pattern = sin(theta * 4.0 + iTime) * cos(r * 10.0 - iTime * 2.0);

    // Color based on angle and time
    vec3 color = hsl2rgb(vec3(
        theta * 0.5 + iTime,
        0.7,
        0.8
    ));

    // Add noise and turbulence
    float noiseValue = turbulent(st * 10.0) * 0.5 + 0.5;
    color *= noiseValue;

    return vec4(color, 1.0);
}
