vec4 Effect2(vec2 uv) {
    vec4 fragColor = vec4(0.0);

    // Add time-based rotation
    float rot = 1.5 * iTime;
    mat2 rotate = mat2(cos(rot), -sin(rot), sin(rot), cos(rot));
    vec2 rotatedUV = rotate(uv * 3.0) / 2.0 + 0.5;

    // Create multiple sine wave layers with different frequencies
    for(float i = 1.0; i <= 3.0; i++) {
        float freq = pow(2.0, i);
        float phaseShift = iTime * i;

        // Calculate sine wave pattern
        vec2 st = rotatedUV * freq + phaseShift;
        float sinVal = sin(st.x * 10.0 + iTime) * cos(st.y * 10.0 + iTime);

        // Add turbulence for more dynamic patterns
        sinVal += turbulent(vec3(rotatedUV, iTime)) * 0.5;

        // Color shift based on frequency
        vec3 color = vec3(0.0);
        color[mod(i, 3.0)] = abs(sinVal) * 0.6;

        // Accumulate colors with alpha blending
        fragColor += vec4(color, abs(sinVal));
    }

    // Normalize and output final color
    fragColor /= 3.0;
    return fragColor;
}
