vec4 Effect2(vec2 uv) {
    // Create a time-varying color shift
    float time = iTime * 0.5;

    // Create a noise-based pattern with turbulence
    vec2 st = uv * 10.0 + time;
    float n = noise(st);
    float t = turbulent(vec3(uv, time));

    // Combine noise and turbulence for dynamic color transitions
    vec3 color = hsl2rgb(vec3(
        (n + t) * 0.5 + 0.5,
        sin(time * 0.1) * 0.5 + 0.5,
        cos(time * 0.1) * 0.5 + 0.5
    ));

    return vec4(color, 1.0);
}
