vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Add time-based rotation and create concentric circles
    a += iTime * 0.5;
    r = mod(r * 8.0 + iTime, 4.0) / 4.0;
    // Create swirling pattern using turbulence
    float t = turbulent(vec3(a, r, iTime * 0.2));

    // Convert to HSL and back to RGB for color transformation
    vec3 hslColor = vec3(
        a * 0.5 + 0.5,
        1.0 - abs(r - 0.5) * 2.0,
        0.8 + t * 0.2
    );

    // Create fading trails effect
    float fade = 1.0 - r * 0.7;
    vec3 color = hsl2rgb(hslColor);

    return vec4(color, max(fade, 0.0));
}
