vec4 Effect2(vec2 uv) {
    // Animate position over time
    vec2 pos = uv * 10.0 + iTime;

    // Create noise pattern with turbulence
    float noiseVal = turbulent(pos);

    // Use HSL to create shifting colors
    vec3 color = hsl(
        mod(iTime * 0.5, 1.0),   // Hue shift over time
        0.7 + noiseVal * 0.3,    // Saturation modulation
        0.5 + sin(pos.x * 0.5) * 0.25 // Lightness variation
    );

    return vec4(color, 1.0);
}
