vec4 Effect2(vec2 uv) {
    // Animate color shifts over time
    float hue = sin(iTime * 0.5 + 1.0) * 0.5 + 0.5;

    // Create sine wave distortion pattern
    vec2 distort = vec2(
        sin(uv.y * 4.0 + iTime) * 0.3,
        cos(uv.x * 4.0 + iTime * 0.7) * 0.3
    );

    // Combine original UV with distortion
    vec2 distortedUV = uv + distort;

    // Add noise for extra detail
    float noiseVal = noise(vec3(distortedUV, iTime));

    // Create plasma effect using HSL to RGB conversion
    vec3 color = hsl(
        hue * 360.0,
        0.7 + sin(iTime) * 0.2,
        0.5 + noiseVal * 0.4
    );

    return vec4(color, 1.0);
}
