vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // Convert UV to polar coordinates for rotation effect
    vec2 st = uv - 0.5;
    float theta = atan2(st.y, st.x) + time;
    float radius = length(st);

    // Create rotating geometric shape using sine waves
    float pattern = sin(theta * 6.0 + time) * 0.5 + 0.5;
    pattern = abs(1.0 - pattern);
    pattern = step(0.5, pattern);

    // Dynamic color transitions using HSL
    vec3 hslColor = vec3((time * 2.0) % 1.0, 0.7, 0.8);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add Perlin noise overlay
    vec2 noiseUV = uv * 5.0 + time;
    float noiseValue = texture(iChannel0, noiseUV).x;
    noiseValue = mix(0.0, 1.0, noiseValue);

    // Combine pattern and noise with color transitions
    vec3 finalColor = mix(vec3(0.1), rgbColor, pattern);
    finalColor = mix(finalColor, vec3(noiseValue), 0.3);

    return vec4(finalColor, 1.0);
}
