vec4 Effect2(vec2 uv) {
    // Create a coordinate system that rotates with time
    vec2 st = uv - 0.5;

    // Add time-based rotation
    float angle = iTime * 0.1;
    vec2 rotated = vec2(
        st.x * cos(angle) - st.y * sin(angle),
        st.x * sin(angle) + st.y * cos(angle)
    );

    // Create a noise pattern with turbulence
    float noiseValue = noise(rotated * 5.0 + iTime);
    float turbulentValue = turbulent(rotated * 10.0 + vec3(iTime));

    // Convert to HSL and back to RGB for color variation
    vec3 color1 = hsl2rgb(vec3(
        fract(iTime * 0.2 + noiseValue * 0.5),
        0.7,
        0.6
    ));

    // Create a second layer of color with offset
    vec3 color2 = hsl2rgb(vec3(
        fract(iTime * 0.3 + noiseValue * 0.7),
        0.8,
        0.5
    ));

    // Combine colors based on turbulence
    vec3 finalColor = mix(color1, color2, turbulentValue * 0.5 + 0.5);

    // Add some white to create cloud-like effect
    finalColor = mix(finalColor, vec3(1.0), noiseValue * 0.5 + 0.3);

    return vec4(finalColor, 1.0);
}
