vec4 Effect2(vec2 uv) {
    // Create movement vector with time offset
    vec2 st = uv * 5.0 + vec2(sin(iTime), cos(iTime)) * 3.0;

    // Add noise-based movement
    vec2 movement = vec2(
        noise(st + vec2(0.0, iTime * 0.5)),
        noise(st + vec2(iTime * 0.5, 0.0))
    ) * 0.4;

    // Calculate base color with time-based hue
    vec3 baseColor = hsl2rgb(vec3(
        fract(iTime * 0.1) * 360.0,
        0.7,
        0.5
    ));

    // Add noise and turbulence for liquid effect
    float liquidNoise = turbulent(st + movement);
    vec3 finalColor = baseColor * (0.5 + liquidNoise * 0.5);

    // Add depth and lighting effects
    finalColor *= mix(
        vec3(1.0),
        vec3(0.3, 0.4, 0.6),
        smoothstep(0.0, 1.0, uv.y)
    );

    // Add specular highlights
    float spec = pow(max(dot(normalize(vec2(sin(iTime), cos(iTime))), vec2(1.0)), 0.0), 30.0);
    finalColor += vec3(spec * 0.5);

    return vec4(finalColor, 1.0);
}
