vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for rotation
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float theta = atan2(st.y, st.x) + sin(iTime * 0.5) * 0.5;

    // Create rotating geometric pattern with noise texture
    float angle = theta + iTime * 0.2;
    float sector = mod(angle * 4.0, PI);
    float shape = turbulent(vec2(r * 10.0, angle)) * 0.5 + 0.5;

    // Dynamic color transitions using HSL
    vec3 hslColor = vec3(
        sin(iTime * 0.3 + sector) * 0.5 + 0.5,
        0.7 + turbulent(vec2(r * 5.0, iTime)) * 0.3,
        0.5 + sin(r * 10.0 + iTime) * 0.5
    );

    // Combine noise texture with color transitions
    vec3 finalColor = hsl2rgb(hslColor) * shape;

    return vec4(finalColor, 1.0);
}
