vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form centered at (0.5, 0.5)
    vec2 st = uv - 0.5;
    float r = length(st);
    float theta = atan2(st.y, st.x) + iTime * 0.5; // Add time-based rotation

    // Create rotating radial waves
    float wave = sin(r * 10.0 + theta * 3.0) * 0.5 + 0.5;

    // Add noise and turbulence for more dynamic effect
    vec2 noisePos = uv * 4.0; // Scale up for higher frequency noise
    float noiseVal = noise(noisePos);
    wave += turbulent(vec3(r, theta, iTime)) * 0.3;

    // Create color fade using HSL
    vec3 hslColor = vec3(
        sin(iTime) * 0.5 + 0.5, // Hue variation over time
        1.0 - wave * 0.5,       // Saturation based on wave value
        0.8 + noiseVal * 0.2    // Lightness with noise modulation
    );

    // Convert HSL to RGB and output
    vec3 rgbColor = hsl2rgb(hslColor);
    return vec4(rgbColor, 1.0);
}
