vec4 Effect2(vec2 uv) {
    // Scale and rotate UV coordinates
    vec2 st = uv * 5.0;

    // Create a grid of hexagons using polar coordinates
    vec2 grid = floor(st + 0.5);
    vec2 center = grid + 0.5;
    vec2 toCenter = st - center;

    // Convert to polar coordinates
    float theta = atan(toCenter.y, toCenter.x) + 0.5 * iTime;
    float r = length(toCenter);

    // Create expanding rings with time-based pulsing
    float ring = sin(r * 10.0 - iTime * 2.0) * 0.5 + 0.5;

    // Add turbulence and noise to the pattern
    vec3 color = hsv2rgb(vec3(
        theta * 0.5 + 0.5 * iTime,
        ring * 0.7 + turbulent(vec3(st, iTime)) * 0.3,
        1.0 - ring * 0.5 - noise(vec3(st, iTime)) * 0.2
    ));

    // Combine the effects and output the final color
    return vec4(color, 1.0);
}
