vec4 Effect2(vec2 uv) {
    // Scale and animate UV coordinates
    vec2 st = uv * 8.0 + iTime;

    // Create multi-scale noise patterns
    float n1 = turbulent(st);
    float n2 = turbulent(st * 0.5 + vec2(123.45, 678.9));
    float n3 = turbulent(st * 0.25 + vec2(987.65, 432.1));

    // Create rotating gradient effect
    vec2 polar = uv - 0.5;
    float angle = atan2(polar.y, polar.x) + iTime * 0.2;
    float radius = length(polar);

    // Combine noise and rotation effects
    vec3 color = hsl2rgb(vec3(
        mod(angle * 0.1 + n1 * 0.5, 1.0),
        0.7 + n2 * 0.3,
        0.8 + n3 * 0.2
    ));

    // Add brightness variation
    color *= 0.8 + n1 * 0.2;

    return vec4(color, 1.0);
}
