vec4 Effect2(vec2 uv) {
    // Create pulsating circles pattern
    vec2 center = (uv - 0.5) * 2.0;
    float dist = length(center);

    // Add time-based pulsation
    float pulse = sin(iTime * 0.5 + dist * 10.0) * 0.5 + 0.5;
    float alpha = smoothstep(0.7, 1.3, dist + pulse + sin(iTime * 2.0) * 0.2);

    // Create color transitions using HSL
    vec3 hslColor = vec3((iTime * 0.1) % 1.0, 1.0, 0.5);
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add noise for extra detail
    float noiseValue = noise(vec2(iTime * 0.1, uv.x * 4.0 + iTime)) * 0.2;

    return vec4(rgbColor * (alpha + noiseValue * 0.3), alpha);
}
