vec4 Effect2(vec2 uv) {
    // Create a grid of rotating triangles
    vec2 st = fract(uv * 10.0); // Tile the space

    // Add rotation based on time
    float angle = sin(iTime + 5.0 * (st.x + st.y)) * 0.5;
    vec2 rotatedUV = cos(angle) * uv - sin(angle) * uv.yx;

    // Create triangle pattern
    vec2 triangle = fract(rotatedUV * 10.0);
    float triangleMask = step(1.0, triangle.x + triangle.y);

    // Add noise overlay
    float noiseValue = noise(uv * 5.0 + iTime);
    vec3 noiseColor = hsl2rgb(vec3(noiseValue * 0.6 + 0.3, 0.8, 0.7));

    // Create color transitions based on time
    vec3 baseColor = hsl2rgb(vec3(iTime * 0.1, 0.5, 0.5));
    vec3 finalColor = mix(baseColor, noiseColor, triangleMask * 0.6);

    return vec4(finalColor, 1.0);
}
