vec4 Effect2(vec2 uv) {
    // Base water color
    vec3 baseColor = hsl2rgb(vec3(190.0/360.0, 0.5, 0.7));

    // Create flowing wave pattern using time and UV coordinates
    float waveSpeed = 0.5;
    vec2 flowPos = uv + vec2(iTime * waveSpeed, iTime * waveSpeed * 0.5);

    // Add wave distortion using sine function
    float wave1 = sin(flowPos.x * 4.0 + iTime) * 0.3;
    float wave2 = sin(flowPos.y * 6.0 + iTime * 0.7) * 0.2;
    float wave3 = turbulent(flowPos * 8.0 + vec2(iTime)) * 0.15;

    // Combine waves
    float totalWave = wave1 + wave2 + wave3;

    // Create flowing pattern by mixing waves with UV coordinates
    vec2 flowPattern = uv + vec2(totalWave, totalWave * 0.5);

    // Add lighting effect based on wave height and direction
    vec3 lightColor = hsl2rgb(vec3(60.0/360.0, 1.0, 1.0));
    float specular = pow(max(0.0, dot(normalize(vec3(ddx(totalWave), ddy(totalWave), 1.0)), 
                                 normalize(vec3(0.0, 0.0, 1.0)))), 20.0);

    // Combine colors and effects
    vec3 finalColor = mix(baseColor, lightColor, specular * 0.4 + 0.6);
    finalColor *= 1.0 - abs(totalWave) * 0.5;

    return vec4(finalColor, 1.0);
}
