vec4 Effect2(vec2 uv) {
    // code here
}
vec2 coord = uv - vec2(0.5);
float r = length(coord);
float theta = atan2(coord.y, coord.x);
theta += iTime * (1.0 + r * 2.0);
float s = abs(sin(theta));
float c = abs(cos(theta));
float sc = (s + c) * 0.5;
theta += iTime * (1.0 + r * 2.0) * 2.5;
s = abs(sin(theta));
c = abs(cos(theta));
float sc2 = (s + c) * 0.5;
float pattern = sc * 3.0 + sc2;
float color = (pattern + 0.5 * (1.0 - r)) * 0.5;
color += length(uv) * 0.2;
vec3 finalColor = hsl(color * 0.3 + 0.1, 0.8, 0.5);
