vec4 Effect2(vec2 uv) {
    // Create a base frequency based on UV coordinates and time
    float freq = 5.0;
    vec3 color1 = sin(uv.x * freq + iTime) * 0.5 + 0.5;
    vec3 color2 = sin(uv.y * freq + iTime * 0.5 + PI) * 0.5 + 0.5;

    // Mix colors using a sine wave pattern
    float mixAmount = sin(iTime * 0.1 + uv.x * 2.0 + uv.y * 3.0);
    vec3 mixedColor = mix(color1, color2, mixAmount);

    // Add turbulence to create an interesting pattern
    vec2 noiseUV = uv * 5.0;
    float turb = turbulent(noiseUV) * 0.5 + 0.5;
    mixedColor *= turb;

    // Create RGB shifts over time
    vec3 rgbShifts = sin(iTime + uv.xxy * 10.0) * 0.5 + 0.5;
    mixedColor = mix(mixedColor, rgbShifts, 0.3);

    // Convert to HSL and back for smooth color transitions
    vec3 hslColor = hsl2rgb(vec3(
        mod(iTime * 0.1 + uv.x * 2.0 + uv.y * 3.0, 1.0) * 360.0,
        0.7,
        0.5
    ));

    // Combine all elements for the final effect
    vec3 finalColor = mix(mixedColor, hslColor, 0.4);

    return vec4(finalColor, 1.0);
}
