vec4 Effect2(vec2 uv) {
    // Center the coordinates
    vec2 centerUv = uv - 0.5;

    // Calculate distance from center
    float dist = length(centerUv);

    // Create expanding/contracting radius using time and sine function
    float radius = sin(iTime * 3.0) * 0.4 + 0.6; // Vary radius between 0.2 and 1.0

    // Add turbulence to create interesting patterns
    float turbulentRadius = turbulent(vec3(dist, iTime, 0.0)) * 0.1;

    // Calculate angle for color variation
    float theta = atan2(centerUv.y, centerUv.x);

    // Create vibrant colors using HSL
    vec3 hslColor = vec3(
        theta / (2.0 * PI) + 0.5 + iTime * 0.1, // Hue
        0.7 + turbulent(vec3(uv, iTime)) * 0.3,   // Saturation
        0.5 + sin(iTime + dist * 10.0) * 0.5     // Lightness
    );

    // Convert HSL to RGB and output
    vec3 color = hsl2rgb(hslColor);

    return vec4(color, 0.8); // Add some transparency for a softer look
}
