vec4 Effect2(vec2 uv) {
    // Create time-dependent animation
    float t = iTime * 0.1;

    // Generate noise patterns with different scales
    vec3 n1 = noise(uv * 5.0 + vec2(t, t));
    vec3 n2 = noise(vec3(uv * 2.0, t));

    // Create turbulence effect
    float t1 = turbulent(uv * 4.0);
    float t2 = turbulent(vec3(uv * 8.0, t));

    // Combine noise and turbulence
    vec3 color = n1 * 0.5 + n2 * 0.5;
    color += vec3(t1 * 0.3, t2 * 0.4, (t1 + t2) * 0.2);

    // Add time-dependent RGB shifts
    color.r += sin(iTime * 0.5 + uv.x * 2.0) * 0.3;
    color.g += sin(iTime * 0.6 + uv.y * 2.0 + 1.0) * 0.3;
    color.b += sin(iTime * 0.7 - uv.x * 2.0 - 1.0) * 0.3;

    // Ensure values stay within [0,1] range
    color = clamp(color, 0.0, 1.0);

    return vec4(color, 1.0);
}
