vec4 Effect2(vec2 uv) {
    vec3 color = vec3(0.0);

    // Create a grid of points
    vec2 st = fract(uv * 20.0);
    vec2 center = floor(st * 10.0 + 0.5) / 10.0;
    float dist = length(center - st);

    // Add pulsation using sine function
    float pulse = sin(iTime * 0.5 + dist * 10.0) * 0.2 + 0.3;

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        (iTime * 0.5 + center.x * 0.1) % 1.0,
        0.7,
        0.5 + pulse * 0.2
    );

    // Convert to RGB and add noise for variation
    vec3 rgbColor = hsl2rgb(hslColor);
    rgbColor *= 1.0 + noise(vec3(uv, iTime)) * 0.2;

    // Draw circles with glow effect
    if (dist < 0.3) {
        color += rgbColor * smoothstep(0.3, 0.0, dist - pulse);
    }

    return vec4(color, 1.0);
}
