vec4 Effect2(vec2 uv) {
    // Create a base pattern using UV coordinates scaled and rotated
    vec2 st = uv * 8.0;
    st += time * 0.5;

    // Create a kaleidoscopic effect by tiling and mirroring
    vec2 tile = floor(st);
    vec2 fract = fract(st);
    vec2 mirror = abs(fract - 0.5) * 2.0;

    // Add noise to create organic patterns
    float n = noise(tile + time);
    mirror += n * 0.3;

    // Create color cycling using HSL
    vec3 color = hsl(
        sin(time * 0.1 + tile.x) * 0.5 + 0.5,
        0.7,
        0.6
    );

    // Add turbulence for dynamic transitions
    float turb = turbulent(mirror * 4.0 + time);
    color *= 1.0 + sin(time * 2.0 + mirror.x * pi) * 0.5;

    return vec4(color, 1.0);
}
