vec4 Effect2(vec2 uv) {
    vec3 mouse = iMouse.xyz;

    // Create base frequency layers with sine waves
    float layer1 = sin(uv.x * 8.0 + iTime) * 0.5 + 0.5;
    float layer2 = sin(uv.y * 4.0 + iTime * 0.5) * 0.3 + 0.3;

    // Add noise and turbulence
    vec2 st = uv * 16.0 + mouse.xy * 8.0;
    float noiseVal = noise(st);
    float turb = turbulent(vec2(uv.x * 4.0, uv.y * 4.0));

    // Combine layers with color cycling
    float hue = (iTime * 0.1) + (noiseVal * 0.5) + (mouse.z * 0.3);
    vec3 color = hsl2rgb(vec3(hue, 1.0, 0.5));

    // Add interactivity with mouse
    float interact = length(uv - mouse.xy) * 0.5;
    color *= mix(1.0, 0.5, smoothstep(0.0, 1.0, interact));

    return vec4(color, 1.0);
}
