vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to range [-1, 1]
    vec2 st = uv * 2.0 - 1.0;

    // Create expanding circle pattern
    float dist = length(st);
    dist += sin(iTime) * 0.5; // Add time-based expansion

    // Generate color using HSL with shifting hues
    vec3 color = hsl(
        mod(dist + iTime, 1.0), // Hue that shifts over time
        0.7, // Saturation
        0.5 + noise(uv * 10.0) * 0.2 // Lightness with noise variation
    );

    // Add turbulence effect to create more dynamic pattern
    float turb = turbulent(vec3(st.x, st.y, iTime));
    color = mix(color, vec3(1.0), abs(turb) * 0.5);

    return vec4(color, 1.0);
}
