vec4 Effect2(vec2 uv) {
    // Convert UV coordinates to polar form for radial effect
    vec2 st = uv * 2.0 - 1.0;
    float theta = atan2(st.y, st.x);
    float r = length(st);
    // Create pulsating animation using time and sine waves
    float timeScale = sin(iTime * 0.5) * 0.5 + 0.5;
    float radiusPulse = sin(r * 10.0 + iTime * 2.0) * 0.3;

    // Add turbulence for dynamic texture
    vec2 noiseUV = uv * 10.0 + iTime;
    float turb = turbulent(noiseUV);

    // Create vibrant color gradients using HSL to RGB conversion
    vec3 hslColor = vec3(
        sin(theta * 5.0 + iTime) * 0.5 + 0.5,  // Hue variation
        0.8 + turb * 0.2,                       // Saturation
        0.5 + radiusPulse                        // Lightness variation
    );

    vec3 rgbColor = hsl2rgb(hslColor);

    // Add radial motion and smooth transitions
    float radialFactor = sin(r * 10.0 + iTime) * 0.5;
    rgbColor *= sin(theta * 5.0 + iTime) * 0.5 + 0.5;

    // Combine all elements for final color
    vec3 finalColor = mix(
        rgbColor,
        vec3(1.0, 0.8, 0.6),  // Warm color overlay
        turb * 0.5
    );

    return vec4(finalColor, 1.0);
}
