vec4 Effect2(vec2 uv) {
    // Create dynamic sine wave patterns with varying frequencies
    float time = iTime * 0.5;

    // Wave components with different frequencies and phases
    float wave1 = sin(uv.x * 4.0 + time) + cos(uv.y * 3.0 + time * 2.0);
    float wave2 = sin(uv.y * 5.0 - time) + cos(uv.x * 2.0 + time * 3.0);
    float wave3 = sin(uv.x * uv.y * 10.0 + time) * cos(time * 4.0);

    // Combine waves with RGB shifts
    vec3 color = vec3(
        sin(wave1 * 2.0 + time) * 0.5 + 0.5,   // Red channel
        sin(wave2 * 2.0 + time * 0.7) * 0.5 + 0.5, // Green channel
        sin(wave3 * 2.0 + time * 1.4) * 0.5 + 0.5  // Blue channel
    );

    // Add turbulence for more dynamic patterns
    vec2 noiseUV = uv * 8.0 + time;
    float turb = turbulent(noiseUV);
    color += turb * 0.3;

    // Apply HSL transformation for better color control
    vec3 hslColor = hsl(color.x, color.y, color.z);
    color = hsl2rgb(hslColor);

    return vec4(color, 1.0);
}
