vec4 Effect2(vec2 uv) {
    float time = iTime * 0.5;

    // X-direction sine wave with frequency modulation
    float freqX = 5.0 + sin(time) * 2.0;
    float waveX = sin(float(freqX) * uv.x * PI * 2.0 + time);

    // Y-direction sine wave with different frequency and phase
    float freqY = 4.0 + cos(time) * 2.0;
    float waveY = sin(float(freqY) * uv.y * PI * 2.0 + time + 1.5708); // Phase shift

    // Combine waves for interference pattern
    float interference = (waveX + waveY) * 0.5;

    // Color shifts using HSL colors
    vec3 colorR = hsl(0.0, 0.7, 0.5 + abs(sin(time + 0.0)));
    vec3 colorG = hsl(0.333, 0.7, 0.5 + abs(cos(time + 1.0)));
    vec3 colorB = hsl(0.666, 0.7, 0.5 + abs(sin(time + 2.0)));

    // Convert to RGB and combine with interference pattern
    vec3 finalColor = 
        hsl2rgb(colorR) * (interference + 1.0)/2.0 +
        hsl2rgb(colorG) * (interference + 1.0)/2.0 +
        hsl2rgb(colorB) * (interference + 1.0)/2.0;

    return vec4(finalColor, 1.0);
}
