vec4 Effect2(vec2 uv) {
    // Rotate and animate UV coordinates
    float time = iTime * 0.5;
    vec2 rotatedUV = vec2(
        cos(time)*uv.x - sin(time)*uv.y,
        sin(time)*uv.x + cos(time)*uv.y
    );

    // Create a grid of tiles with noise patterns
    vec2 tileCoords = rotatedUV * 10.0;
    vec2 tilePos = fract(tileCoords);

    // Generate turbulence pattern within each tile
    float turb = turbulent(vec3(tilePos, time));

    // Create color transitions using HSL
    vec3 hslColor = vec3(
        mod(tileCoords.x + time, 1.0) * 2.0,
        0.7,
        0.6
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Add glow effect based on turbulence
    float glow = smoothstep(0.5, 1.0, turb + length(tilePos - 0.5));

    // Combine color and glow for final output
    return vec4(rgbColor * (1.0 + glow * 0.5), 1.0);
}
