vec4 Effect2(vec2 uv) {
    // Create base frequency components
    float freq = 5.0;

    // Add time-based movement
    vec3 color = vec3(
        sin(uv.x * freq + iTime) + sin(uv.y * freq/2.0 + iTime*0.5),
        sin(uv.x * freq*1.5 + iTime*0.7) + sin(uv.y * freq*1.2 + iTime),
        sin(uv.x * freq*2.0 + iTime*1.4) + sin(uv.y * freq*0.8 + iTime*0.3)
    );

    // Add RGB shifts
    vec3 offsets = vec3(
        sin(iTime * 0.2) * 0.5 + 0.5,
        sin(iTime * 0.3 + PI) * 0.5 + 0.5,
        sin(iTime * 0.4 + PI*2.0) * 0.5 + 0.5
    );

    // Add turbulence for organic movement
    float turb = turbulent(vec3(uv, iTime)) * 0.1;

    // Combine everything and normalize to [0,1] range
    color += sin(offsets + uv.xxy * vec3(2.0, 3.0, 4.0) + iTime) + turb;
    color = (color * 0.5) + 0.5; // Normalize

    return vec4(color, 1.0);
}
