vec4 Effect2(vec2 uv) {
    // Convert UV to polar coordinates
    vec2 st = uv * 2.0 - 1.0;
    float r = length(st);
    float a = atan2(st.y, st.x);
    // Animate rotation and radius
    float time = iTime * 0.5;
    a += time;
    r *= 0.7 + turbulent(vec3(r, a, time)) * 0.3;
    // Create orbiting shapes using sine waves
    float shape = sin(a * 8.0 + time) * 0.5 + 0.5;
    shape = mix(shape, abs(sin(r * 10.0 - time)), turbulent(vec2(r, a)) * 0.3);
    // Color transitions using HSL
    vec3 color = hsl2rgb(vec3(
        (sin(time) * 0.5 + 0.5) * 360.0,
        1.0,
        0.5 + turbulent(vec3(r, a, time)) * 0.5
    ));
    // Combine elements and output
    float final = mix(shape, r, 0.2);
    return vec4(color * (final), 1.0);
}
