vec4 Effect2(vec2 uv) {
    // Create flowing motion by offsetting UV with time
    vec2 st = uv * 5.0 + iTime * 0.1;

    // Add turbulence for dynamic flow patterns
    float flow = turbulent(st + sin(iTime)*3.0);
    flow += turbulent(st*0.5 + cos(iTime)*2.0) * 0.5;

    // Create color shifts using HSL
    vec3 color = hsl(
        mod(flow * 10.0 + iTime, 1.0),   // Hue variation
        0.7 + flow * 0.3,                // Saturation
        0.5 + sin(iTime) * 0.2           // Lightness modulation
    );

    // Add metallic sheen using noise patterns
    float metal = noise(vec3(uv*10.0, iTime)) * 0.5 + 0.5;
    color *= hsl2rgb(vec3(0.0, 0.0, metal));

    // Create flowing gradient effect
    vec2 flowUV = uv * 4.0 + iTime;
    float gradient = turbulent(flowUV) * 0.5 + 0.5;

    // Combine effects with depth perception
    vec3 finalColor = mix(
        color,
        hsl2rgb(vec3(0.0, 0.0, 1.0)),  // Add highlight effect
        gradient * 0.8
    );

    return vec4(finalColor, 1.0);
}
