vec4 Effect2(vec2 uv) {
    // Create a grid of points
    vec2 st = uv * 10.0;
    vec2 ipos = floor(st);
    vec2 fpos = fract(st);

    // Calculate rotation angle based on position and time
    float angle = iTime * (dot(ipos, vec2(1.0, 1.0)) + 1.0);

    // Convert to polar coordinates for hexagonal pattern
    vec2 uvPolar;
    uvPolar.x = length(fpos - 0.5);
    uvPolar.y = atan2(fpos.y - 0.5, fpos.x - 0.5) + angle;

    // Create hexagonal shape using polar coordinates
    float d = max(abs(uvPolar.x * cos(uvPolar.y)), abs(uvPolar.x * sin(uvPolar.y)));

    // Add noise to create variation in rotation and color
    angle += noise(vec3(uv * 5.0, iTime)) * 0.2;

    // Create pulsating RGB colors using HSL
    float hue = iTime * 0.2 + length(ipos) * 0.1;
    vec3 color = hsl(hue, 1.0, 0.5);

    // Create stroke effect for hexagons
    float alpha = 1.0 - smoothstep(0.45, 0.5, d);

    return vec4(color * (1.0 - noise(vec3(uv * 2.0, iTime)) * 0.3), alpha);
}
