vec4 Effect2(vec2 uv) {
    // Create a grid of light rays with time-based movement
    vec2 st = uv * 10.0 + sin(iTime) * 5.0;

    // Add distortion using noise and turbulence
    vec3 noiseDistort = vec3(st.x, st.y, iTime * 0.5);
    float distort = turbulent(noiseDistort) * 0.5 + 0.5;

    // Create a kaleidoscopic pattern by mirroring coordinates
    vec2 mirroredUv = fract(abs(uv * 4.0)) * 2.0 - 1.0;

    // Add color variation using HSL to RGB conversion
    vec3 hslColor = vec3(
        distort * 360.0,
        0.7 + sin(iTime) * 0.3,
        0.5 + cos(iTime * 0.5) * 0.5
    );
    vec3 rgbColor = hsl2rgb(hslColor);

    // Create radial light rays effect
    float distanceFromCenter = length(uv - 0.5);
    float lightIntensity = (1.0 - smoothstep(0.0, 0.8, distanceFromCenter)) * 2.0;

    // Combine all elements for the final effect
    vec3 finalColor = rgbColor * lightIntensity;

    return vec4(finalColor, 1.0);
}
